// Script to generate a simple test image (100x100 blue square PNG)
const fs = require('fs')
const path = require('path')

// Base64 encoded 100x100 blue PNG image
const base64Image = ''

const imagePath = path.join(__dirname, 'test-avatar.png')
const imageBuffer = Buffer.from(base64Image, 'base64')

fs.writeFileSync(imagePath, imageBuffer)
console.log('Test image created:', imagePath)
