    pm.expect(pm.response.code).to.equal(204);
    console.log("‚úÖ Resource deleted successfully");
});

pm.test("‚ö° Performance: Response time acceptable", function () {
    const responseTime = pm.response.responseTime;
    const maxTime = pm.environment.get("max_response_time") || 5000;

    pm.expect(responseTime).to.be.below(maxTime);
    console.log("‚ö° Response time: " + responseTime + "ms (max: " + maxTime + "ms)");

    // Track performance for regression detection
    const perfKey = "perf_step_56";
    const previousTime = pm.environment.get(perfKey);
    pm.environment.set(perfKey, responseTime);

    if (previousTime) {
        const increase = ((responseTime - previousTime) / previousTime) * 100;
        if (increase > 50) {
            console.warn("‚ö†Ô∏è Performance regression: " + increase.toFixed(1) + "% slower than previous run");
        }
    }
});

// ===== WORKFLOW SUMMARY =====
console.log("üìä Workflow Execution Summary");
console.log("Total Steps: undefined");

// Calculate total execution time
const startTime = pm.globals.get("workflow_start_time");
if (startTime) {
    const totalTime = Date.now() - startTime;
    console.log("‚è±Ô∏è Total Execution Time: " + totalTime + "ms");
    pm.globals.set("workflow_total_time", totalTime);
}

// Summary of step performance
console.log("üìà Performance Summary:");
for (let i = 1; i <= undefined; i++) {
    const stepTime = pm.environment.get("perf_step_" + i);
    if (stepTime) {
        console.log("  Step " + i + ": " + stepTime + "ms");
    }
}

console.log("‚úÖ Workflow completed successfully");

