requiredVars.forEach(varName => {
    const value = pm.environment.get(varName);
    if (!value || value === '') {
        console.error("‚ùå CRITICAL: Required environment variable '" + varName + "' is not set for " + method + " operation");
        hasAllRequiredVars = false;
