        varPrefix = "dsl"; // DSL transaction
    } else if (currentUrl.includes("/transactions/inflow")) {
        varPrefix = "inflow"; // Inflow transaction
    } else if (currentUrl.includes("/transactions/outflow")) {
        varPrefix = "outflow"; // Outflow transaction
    }

    // Store transaction ID with appropriate prefix
