    const operationIdVar = varPrefix ? varPrefix + "OperationId" : "operationId";
    const balanceIdVar = varPrefix ? varPrefix + "BalanceId" : "balanceId";
    const accountIdVar = varPrefix ? varPrefix + "AccountId" : "accountId";

    pm.environment.set(transactionIdVar, jsonData.id);
    console.log("üíæ Stored " + transactionIdVar + ":", jsonData.id);

    // Extract operation and balance IDs if available
